mapper.insert:

	INSERT INTO BBS 
		( 
			TITLE
		  , CONTENT
		  , CREATOR
		  , PWD
		  
		  , BBS_TYPE
		  , START_DATE
		  , END_DATE
		  
		 )
	VALUES (
			#{title}
		  , #{content}
		  , #{creator}
		  , #{pwd}
		  
		  , #{bbsType}
		  , #{startDate}
		  , #{endDate}
		  
		   )	
	
		SELECT CURRVAL('bbs_cnt_id') AS "bbsId";


	
	INSERT INTO BBS_FILE 
		( 
			ORI_FILE_NAME
		  , NEW_FILE_NAME
		  , BBS_ID
		 )
	VALUES
	
			#{file.fileName}
		  , #{file.newFileName}
		  , #{file.bbsId}

mapper.update:

	UPDATE BBS
	SET CNT_ID=#{bbsId}
		, TITLE=#{title}
		, CONTENT=#{content}
	  
		, BBS_TYPE = #{bbsType}
		, START_DATE = #{startDate}
		, END_DATE = #{endDate}
	   
	WHERE CNT_ID=#{bbsId}

mapper.select:
SELECT FILE_SEQ , ORI_FILE_NAME , NEW_FILE_NAME FROM BBS_FILE WHERE BBS_ID=#{bbsId}
AND FILE_SEQ = #{fileSeq}
ORDER BY FILE_SEQ

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT row_number() over() as ROWNUM , CNT_ID , TITLE , CONTENT , CREATOR , VIEW_COUNT , CREATE_DATETIME , PWD , START_DATE , END_DATE , (SELECT COUNT(*) FROM BBS_TAIL WHERE BBS_ID = BBS.CNT_ID) AS TAIL_COUNT , (SELECT COUNT(*) FROM BBS_FILE WHERE BBS_ID = BBS.CNT_ID) AS FILE_COUNT FROM BBS WHERE 1 = 1 AND TITLE LIKE '%' || #{searchKeyword} || '%'
AND CONTENT LIKE '%' || #{searchKeyword} || '%'
AND CREATOR LIKE '%' || #{searchKeyword} || '%'
AND bbs_type = #{bbsType}
AND bbs_type is null
AND START_DATE <= #{startDate}
AND END_DATE >= #{endDate}
ORDER BY CNT_ID DESC LIMIT #{lastIndex} ) A WHERE ROWNUM <= #{lastIndex} ) B WHERE RNUM > #{firstIndex}
