mapper.update:

UPDATE BBS_TAIL
SET CNT_ID=#{bbsTailId}
	, CONTENT=#{content}
WHERE CNT_ID=#{bbsTailId}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT row_number() over() as ROWNUM , CNT_ID , CONTENT , CREATOR , CREATE_DATETIME FROM BBS_TAIL WHERE 1 = 1 AND CNT_ID = #{searchKeyword}
AND CONTENT LIKE '%' || #{searchKeyword} || '%'
AND CREATOR LIKE '%' || #{searchKeyword} || '%'
ORDER BY CNT_ID DESC
) A WHERE ROWNUM <= #{lastIndex} ) B WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM BBS_TAIL WHERE 1 = 1 AND CNT_ID = #{searchKeyword}
AND CONTENT LIKE '%' || #{searchKeyword} || '%'
AND CREATOR LIKE '%' || #{searchKeyword} || '%'
